pragma experimental ABIEncoderV2;

import "../interfaces/IRewarderV2.sol";
import "../codes/Ownable.sol";

contract TotalRewarder is Ownable {

  struct RewarderInfo {
    address rewardToken;
    uint256 tokenPerBlock;
    uint256 allocPoint;
    uint256 totalAllocPoint;
  }

  mapping(uint256 => IRewarderV2[]) public poolRewarderInfo;

  address public masterChef;

  constructor (address masterChef_) public {
    masterChef = masterChef_;
  }

  // EVENT

  event LogAddPoolRewarder(uint256 pid, address newRewarder);
  event LogRemovePoolRewarder(uint256 pid, address rewarder);

  // MODIFIER

  modifier onlyMC() {
    require(msg.sender == masterChef, "TotalRewarder:: Not from master chef");
    _;
  }

  // VIEW FUNCTION 

  /// @notice Returns the number of rewarder in pool.
  /// @param pid Pool id.
  function poolRewarderLength(uint256 pid) public view returns (uint256) {
    return poolRewarderInfo[pid].length;
  }

  /// @notice Function to see user's pending amounts for pools reward.
  /// @param pid Pool id.
  /// @param user Address of user.
  /// @param claRewardAmount Amount of cla reward.
  function pendingTokens(
    uint256 pid,
    address user,
    uint256 claRewardAmount
  ) external view returns (IERC20[] memory, uint256[] memory) {
    uint256 length = poolRewarderInfo[pid].length;
    IERC20[] memory rewardTokens = new IERC20[](length);
    uint256[] memory rewardAmounts = new uint256[](length);
    for(uint8 i = 0; i < length; i++) {
      (IERC20[] memory _rewardTokens, uint256[] memory _rewardAmounts) = poolRewarderInfo[pid][i].pendingTokens(pid, user, claRewardAmount);
      rewardTokens[i] = _rewardTokens[0];
      rewardAmounts[i] = _rewardAmounts[0];
    }
    return (rewardTokens, rewardAmounts);
  }

  /// @notice Function to see pool's all rewarder info.
  /// @param pid Pool id.
  function getRewarderInfo(uint256 pid) public view returns (RewarderInfo[] memory) {
    uint256 length = poolRewarderInfo[pid].length;
    RewarderInfo[] memory rewarderInfo = new RewarderInfo[](length);
    for (uint8 i = 0; i < length; i++) {
      rewarderInfo[i] = RewarderInfo({
        rewardToken: poolRewarderInfo[pid][i].rewardToken(),
        tokenPerBlock: poolRewarderInfo[pid][i].tokenPerBlock(),
        allocPoint: poolRewarderInfo[pid][i].poolInfo(pid).allocPoint,
        totalAllocPoint: poolRewarderInfo[pid][i].totalAllocPoint()
      });
    }

    return rewarderInfo;
  }

  // STATE MODIFYING FUNCTION

  /// @notice Function to claim all user's reward from pool rewarder.
  /// Only MasterChef can call this function.
  /// @param pid Pool id.
  /// @param user Address of user.
  /// @param recipient Address to recieve reward.
  /// @param claAmount Amount of cla reward.
  /// @param newLpAmount Amount of user lp amount.
  function onClaReward(
    uint256 pid, 
    address user, 
    address recipient, 
    uint256 claAmount, 
    uint256 newLpAmount
  ) external onlyMC {
    for(uint8 i = 0; i < poolRewarderInfo[pid].length; i++) {
      poolRewarderInfo[pid][i].onClaReward(pid, user, recipient, claAmount, newLpAmount);
    }
  }

  // ADMIN FUNCTION

  function addPoolRewarder(uint256 pid, IRewarderV2 newRewarder) public onlyOwner {
    require(address(newRewarder) != address(0), "TotalRewarder:: rewarder can not be zero address");
    IRewarderV2[] storage poolRewarders = poolRewarderInfo[pid];
    for (uint8 i = 0; i < poolRewarders.length; i++) {
      // duplicate check
      require(address(poolRewarders[i]) != address(newRewarder), "TotalRewarder:: Already added");
    }
    poolRewarders.push(newRewarder);
    emit LogAddPoolRewarder(pid, address(newRewarder));
  }

  function removePoolRewarder(uint256 pid, IRewarderV2 rewarder) public onlyOwner {
    bool flag;
    IRewarderV2[] storage poolRewarders = poolRewarderInfo[pid];
    for (uint8 i = 0; i < poolRewarders.length; i++) {
        if (!flag && address(poolRewarders[i]) == address(rewarder)) {
            flag = true;
            emit LogRemovePoolRewarder(pid, address(rewarder));
        }
        if (flag) {
            if (i < poolRewarders.length -1) {
                poolRewarders[i] = poolRewarders[i+1];
            } else {
                delete poolRewarders[i];
                poolRewarders.length--;
            }
        }
    }
  }
}
